// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.22;
//Inspired by Uniswap + accross' ERC7683 Draft standard
contract ERC7683  {
   /// @title GaslessCrossChainOrder CrossChainOrder type
/// @notice Standard order struct to be signed by users, disseminated to fillers, and submitted to origin settler contracts
struct GaslessCrossChainOrder {
	/// @dev The contract address that the order is meant to be settled by.
	/// Fillers send this order to this contract address on the origin chain
	address originSettler;
	/// @dev The address of the user who is initiating the swap,
	/// whose input tokens will be taken and escrowed

	address user;
	/// @dev Nonce to be used as replay protection for the order
	uint256 nonce;
	/// @dev The chainId of the origin chain
	uint256 originChainId;
	/// @dev The timestamp by which the order must be opened
	uint32 openDeadline;
	/// @dev The timestamp by which the order must be filled on the destination chain
	uint32 fillDeadline;
	/// @dev Type identifier for the order data. This is an EIP-712 typehash.
	uint256 orderDataType;
	/// @dev Arbitrary implementation-specific data
	/// Can be used to define tokens, amounts, destination chains, fees, settlement parameters,
	/// or any other order-type specific information
    //CUSTOM TWEAKS FOR HACKATHON
   
    address destinationChain;
    // @dev Source ERC-20 token using for swap
    address SourceToken;
    //@dev destination ERC-20 token using for swap
    address DestinationToken;
    //@dev origin token Amount to be bridged/swapped
    uint256 tokenAmount;
    //@dev Destination token Amount to be recieved based on pre-calc
    uint256 destinationTokenAmount;
    //@dev Destination chainId
    uint32 destinationChainId;
    //@dev logic for verification solution
    bool isVerifyMessage;



}

/// @title OnchainCrossChainOrder CrossChainOrder type
/// @notice Standard order struct for user-opened orders, where the user is the msg.sender.
struct OnchainCrossChainOrder {
	/// @dev The timestamp by which the order must be filled on the destination chain
	uint32 fillDeadline;
	/// @dev Type identifier for the order data. This is an EIP-712 typehash.
	bytes32 orderDataType;
	/// @dev Arbitrary implementation-specific data
	/// Can be used to define tokens, amounts, destination chains, fees, settlement parameters,
	/// or any other order-type specific information
	bytes orderData;
}
    /// @notice Tokens that must be receive for a valid order fulfillment
    struct Output {
        /// @dev The address of the ERC20 token on the destination chain
        /// @dev address(0) used as a sentinel for the native token
        address token;
        /// @dev The amount of the token to be sent
        uint256 amount;
        /// @dev The address to receive the output tokens
        bytes32 recipient;
        /// @dev The destination chain for this output
        uint256 chainId;
    }

    /// @title FillInstruction type
    /// @notice Instructions to parameterize each leg of the fill
    /// @dev Provides all the origin-generated information required to produce a valid fill leg
    struct FillInstruction {
        /// @dev The contract address that the order is meant to be settled by
        uint64 destinationChainId;
        /// @dev The contract address that the order is meant to be filled on
        bytes32 destinationSettler;
        /// @dev The data generated on the origin chain needed by the destinationSettler to process the fill
        bytes originData;
    }

    /// @notice Initiates the settlement of a cross-chain order
	/// @dev To be called by the filler
	/// @param order The CrossChainOrder definition
	/// @param fillerData Any filler-defined data required by the settler
	function initiate(GaslessCrossChainOrder memory order, bytes memory fillerData) external  pure virtual{}
    

    /// @notice Fills a single leg of a particular order on the destination chain
	/// @param orderId Unique order identifier for this order
	/// @param originData Data emitted on the origin to parameterize the fill
	/// @param fillerData Data provided by the filler to inform the fill or express their preferences
	function fill(bytes32 orderId, bytes calldata originData, bytes calldata fillerData)external pure virtual  {}

}

contracts/Settlement.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.22;

import {OApp, Origin, MessagingFee} from "@layerzerolabs/oapp-evm/contracts/oapp/OApp.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {OptionsBuilder} from "@layerzerolabs/oapp-evm/contracts/oapp/libs/OptionsBuilder.sol";
import {ERC7683} from "./ERC7683.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract Settlement is OApp, ERC7683 {
    using SafeERC20 for IERC20;

    constructor(
        address _endpoint,
        address _owner,
        bytes memory Option,
        uint32 solverDaoEid
    ) OApp(_endpoint, _owner) Ownable(_owner) {
        defaultOption = Option;
        SolverDAOdsteID = solverDaoEid;
    }

    // Some arbitrary data you want to deliver to the destination chain!
    string public data;
    bytes public defaultOption;

    mapping(uint256 => GaslessCrossChainOrder) orders;
    mapping(uint256 => string) orderStore;
    mapping(uint256 => OrderStatus) orderStatuses;

    uint32 public SolverDAOdsteID;

    enum OrderStatus {
        INITIATED,
        PENDING,
        VERIFIED,
        FILLED,
        CANCELLED
    }

    /**
     * @notice Sends a message from the source to destination chain.
     * @param _dstEid Destination chain's endpoint ID.
     * @param _message The message to send.
     */
    function send(uint32 _dstEid, uint256 _message) external payable {
        // Encodes the message before invoking _lzSend.
        // Replace with whatever data you want to send!
        bytes memory _payload = abi.encode(_message);
        _lzSend(
            _dstEid,
            _payload,
            defaultOption,
            // Fee in native gas and ZRO token.
            MessagingFee(msg.value, 0),
            // Refund address in case of failed source message.
            payable(msg.sender)
        );
    }

    function setPeerWithAddress(uint32 _eid, address _peer) public onlyOwner {
        bytes32 _add = bytes32(uint256(uint160(_peer)));
        _setPeer(_eid, _add);
    }

    /**
     * @dev Called when data is received from the protocol. It overrides the equivalent function in the parent contract.
     * Protocol messages are defined as packets, comprised of the following parameters.
     * @param _origin A struct containing information about where the packet came from.
     * @param _guid A global unique identifier for tracking the packet.
     * @param payload Encoded message.
     */
    function _lzReceive(
        Origin calldata _origin,
        bytes32 _guid,
        bytes calldata payload,
        address, // Executor address as specified by the OApp.
        bytes calldata // Any extra data or options to trigger on receipt.
    ) internal override {
        // Decode the payload to get the message
        // In this case, type is string, but depends on your encoding!

        (GaslessCrossChainOrder memory order, address filler) = abi.decode(
            payload,
            (GaslessCrossChainOrder, address)
        );
        if (order.isVerifyMessage == true) {
            orderStatuses[order.nonce] = OrderStatus.VERIFIED;
        }
        //transfer Source token to the filler
        else {
            orderStatuses[order.nonce] = OrderStatus.FILLED;

            IERC20(order.SourceToken).transferFrom(
                order.originSettler,
                filler,
                order.tokenAmount
            );
        }

        // _lzSend(
        //     SolverDAOdsteID,
        //     payload,
        //     defaultOption,
        //     // Fee in native gas and ZRO token.
        //     MessagingFee(msg.value, 0),
        //     // Refund address in case of failed source message.
        //     payable(msg.sender)
        // );
    }

    function initiate(
        GaslessCrossChainOrder calldata order,
        bytes calldata signature,
        bytes calldata fillerData
    ) external {
        //verify Signature Logic

        orderStatuses[order.nonce] = OrderStatus.INITIATED;

        //ERC20 transfer and lock begins
        verifySettlement(order);
        IERC20(order.SourceToken).safeTransferFrom(
            order.user,
            address(this),
            order.tokenAmount
        );
    }

    function verifySettlement(GaslessCrossChainOrder calldata order) private {
        orderStatuses[order.nonce] = OrderStatus.PENDING;
    }

    function fill(
        GaslessCrossChainOrder calldata order,
        bytes calldata fillerData
    ) external payable {
        if (orderStatuses[order.nonce] == OrderStatus.FILLED)
            revert("AlreadyFilled");
        if(orderStatuses[order.nonce]!= OrderStatus.VERIFIED) revert("Wait till your Solution is verified");

        (address filler, uint256 fee) = abi.decode(
            fillerData,
            (address, uint256)
        );

        //transfer Destinationtoken to user/swapper
        IERC20(order.DestinationToken).transferFrom(
            filler,
            order.user,
            order.destinationTokenAmount - fee
        );

        // Encodes the message before invoking _lzSend.
        // Replace with whatever data you want to send!
        bytes memory _payload = abi.encode(order, filler);
        _lzSend(
            order.destinationChainId,
            _payload,
            defaultOption,
            // Fee in native gas and ZRO token.
            MessagingFee(msg.value, 0),
            // Refund address in case of failed source message.
            payable(msg.sender)
        );
    }

    /* @dev Quotes the gas needed to pay for the full omnichain transaction.
     * @return nativeFee Estimated gas fee in native gas.
     * @return lzTokenFee Estimated gas fee in ZRO token.
     */
    function quote(
        uint32 _dstEid, // Destination chain's endpoint ID.
        string memory _message, // The message to send.
        bool _payInLzToken // boolean for which token to return fee in
    ) public view returns (uint256 nativeFee, uint256 lzTokenFee) {
        bytes memory _payload = abi.encode(_message);
        MessagingFee memory fee = _quote(
            _dstEid,
            _payload,
            defaultOption,
            _payInLzToken
        );
        return (fee.nativeFee, fee.lzTokenFee);
    }

    function getOrderStatus(uint256 nonce)
        public
        view
        returns (OrderStatus status)
    {
        return orderStatuses[nonce];
    }

    function withdraw(address token) public {
        IERC20(token).transfer(
            msg.sender,
            IERC20(token).balanceOf(address(this))
        );
    }
}

contracts/SolverDAO.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.22;
import {OApp, Origin, MessagingFee} from "@layerzerolabs/oapp-evm/contracts/oapp/OApp.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {OptionsBuilder} from "@layerzerolabs/oapp-evm/contracts/oapp/libs/OptionsBuilder.sol";
import  "./ERC7683.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract SolverDAO is OApp,ERC7683 {
    constructor(
        address _endpoint,
        address _owner,
        bytes memory Option,
        uint256 minStakeAmount
    ) OApp(_endpoint, _owner) Ownable(_owner) {
        defaultOption = Option;
        minStakeAmountval = minStakeAmount;
    }

    bytes public defaultOption;
    mapping(address => Solver) public solvers;
    mapping(uint256=> bool) solutionState;
    uint256 public intentCount;
    uint256 public minStakeAmountval;

    struct Solver {
        uint256 stakedAmount;
        bool isActive;
        uint256 reputation; // Optional: track solver reputation
    }

    function stake() external payable {
        require(msg.value >= minStakeAmountval, "Insufficient staking amount");
        solvers[msg.sender].stakedAmount += msg.value;
        solvers[msg.sender].isActive = true;
    }

    function postSolution(GaslessCrossChainOrder memory order, address filler,bytes memory solution) public payable{
        if(solvers[msg.sender].isActive == false) revert("First try staking");

        verifySolution(order.nonce,solution);
        order.isVerifyMessage = true;
        bytes memory _payload = abi.encode(order,filler);
         _lzSend(
            order.destinationChainId,
            _payload,
            defaultOption,
            // Fee in native gas and ZRO token.
            MessagingFee(msg.value, 0),
            // Refund address in case of failed source message.
            payable(msg.sender)
        );
        

    }

    //arbitary verification Logic // Opens endless possibilites for verification
    //For hackathon scope returning true
    function verifySolution(uint256 nonce, bytes memory solution) internal returns (bool) {
        solutionState[nonce] =  true;
        return true;
    }

    function _lzReceive(
        Origin calldata _origin,
        bytes32 _guid,
        bytes calldata payload,
        address, // Executor address as specified by the OApp.
        bytes calldata // Any extra data or options to trigger on receipt.
    ) internal override {
        // Decode the payload to get the message
        // In this case, type is string, but depends on your encoding!
        
    }

     function quote(
        uint32 _dstEid, // Destination chain's endpoint ID.
        string memory _message, // The message to send.
        bool _payInLzToken // boolean for which token to return fee in
    ) public view returns (uint256 nativeFee, uint256 lzTokenFee) {
        bytes memory _payload = abi.encode(_message);
        MessagingFee memory fee = _quote(
            _dstEid,
            _payload,
            defaultOption,
            _payInLzToken
        );
        return (fee.nativeFee, fee.lzTokenFee);
    }

        function setPeerWithAddress(uint32 _eid, address _peer) public onlyOwner {
        bytes32 _add = bytes32(uint256(uint160(_peer)));
        _setPeer(_eid, _add);
    }

    function getState(uint256 nonce)public view returns(bool){
        return solutionState[nonce];
    }

    function getStakedStatus(address solver) public view returns(bool){
        return solvers[solver].isActive;
    }
}

